<!DOCTYPE html>
<html lang="fa">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>گالری تصاویر</title>
    <!-- PhotoSwipe CSS از CDN -->
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/photoswipe/4.1.3/photoswipe.min.css">
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/photoswipe/4.1.3/default-skin/default-skin.min.css">
</head>
<body>
    <!-- گالری تصاویر -->
    <div class="my-gallery" itemscope itemtype="http://schema.org/ImageGallery">
        <figure itemprop="associatedMedia" itemscope itemtype="http://schema.org/ImageObject">
            <a href="data:image/jpeg;base64," itemprop="contentUrl" data-size="1200x800">
                <img src="data:image/jpeg;base64," itemprop="thumbnail" alt="تصویر 1" />
            </a>
            <figcaption itemprop="caption description">توضیحات تصویر 1</figcaption>
        </figure>
        <figure itemprop="associatedMedia" itemscope itemtype="http://schema.org/ImageObject">
            <a href="data:image/jpeg;base64," itemprop="contentUrl" data-size="1200x800">
                <img src="data:image/jpeg;base64," itemprop="thumbnail" alt="تصویر 2" />
            </a>
            <figcaption itemprop="caption description">توضیحات تصویر 2</figcaption>
        </figure>
        <!-- تصاویر بیشتر -->
    </div>

    <!-- PhotoSwipe container -->
    <div class="pswp" tabindex="-1" role="dialog" aria-hidden="true">
        <div class="pswp__bg"></div>
        <div class="pswp__scroll-wrap">
            <div class="pswp__container">
                <div class="pswp__item"></div>
                <div class="pswp__item"></div>
                <div class="pswp__item"></div>
            </div>
            <div class="pswp__ui pswp__ui--hidden">
                <div class="pswp__top-bar">
                    <div class="pswp__counter"></div>
                    <button class="pswp__button pswp__button--close" title="بستن"></button>
                    <button class="pswp__button pswp__button--share" title="اشتراک‌گذاری"></button>
                    <button class="pswp__button pswp__button--fs" title="تمام‌صفحه"></button>
                    <button class="pswp__button pswp__button--zoom" title="زوم"></button>
                    <div class="pswp__preloader">
                        <div class="pswp__preloader__icn">
                            <div class="pswp__preloader__cut">
                                <div class="pswp__preloader__donut"></div>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="pswp__share-modal pswp__share-modal--hidden pswp__single-tap">
                    <div class="pswp__share-tooltip"></div>
                </div>
                <button class="pswp__button pswp__button--arrow--left" title="قبلی"></button>
                <button class="pswp__button pswp__button--arrow--right" title="بعدی"></button>
                <div class="pswp__caption">
                    <div class="pswp__caption__center"></div>
                </div>
            </div>
        </div>
    </div>

    <!-- jQuery از CDN -->
    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
    <!-- PhotoSwipe JS از CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/photoswipe/4.1.3/photoswipe.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/photoswipe/4.1.3/photoswipe-ui-default.min.js"></script>

    <!-- اسکریپت راه‌اندازی PhotoSwipe -->
    <script>
        $(document).ready(function() {
            var initPhotoSwipeFromDOM = function(gallerySelector) {
                var parseThumbnailElements = function(el) {
                    var thumbElements = el.childNodes,
                        numNodes = thumbElements.length,
                        items = [],
                        figureEl,
                        linkEl,
                        size,
                        item;

                    for (var i = 0; i < numNodes; i++) {
                        figureEl = thumbElements[i]; // <figure> element

                        if (figureEl.nodeType !== 1) {
                            continue;
                        }

                        linkEl = figureEl.children[0]; // <a> element
                        size = linkEl.getAttribute('data-size').split('x');

                        // ایجاد شیء اسلاید
                        item = {
                            src: linkEl.getAttribute('href'),
                            w: parseInt(size[0], 10),
                            h: parseInt(size[1], 10)
                        };

                        if (figureEl.children.length > 1) {
                            item.title = figureEl.children[1].innerHTML; // محتوای <figcaption>
                        }

                        if (linkEl.children.length > 0) {
                            item.msrc = linkEl.children[0].getAttribute('src'); // آدرس تصویر کوچک
                        }

                        item.el = figureEl; // ذخیره لینک به عنصر برای getThumbBoundsFn
                        items.push(item);
                    }

                    return items;
                };

                var closest = function closest(el, fn) {
                    return el && (fn(el) ? el : closest(el.parentNode, fn));
                };

                var onThumbnailsClick = function(e) {
                    e = e || window.event;
                    e.preventDefault ? e.preventDefault() : e.returnValue = false;

                    var eTarget = e.target || e.srcElement;

                    var clickedListItem = closest(eTarget, function(el) {
                        return (el.tagName && el.tagName.toUpperCase() === 'FIGURE');
                    });

                    if (!clickedListItem) {
                        return;
                    }

                    var clickedGallery = clickedListItem.parentNode,
                        childNodes = clickedListItem.parentNode.childNodes,
                        numChildNodes = childNodes.length,
                        nodeIndex = 0,
                        index;

                    for (var i = 0; i < numChildNodes; i++) {
                        if (childNodes[i].nodeType !== 1) {
                            continue;
                        }

                        if (childNodes[i] === clickedListItem) {
                            index = nodeIndex;
                            break;
                        }
                        nodeIndex++;
                    }

                    if (index >= 0) {
                        openPhotoSwipe(index, clickedGallery);
                    }
                    return false;
                };

                var openPhotoSwipe = function(index, galleryElement, disableAnimation) {
                    var pswpElement = document.querySelectorAll('.pswp')[0],
                        gallery,
                        options,
                        items;

                    items = parseThumbnailElements(galleryElement);

                    options = {
                        index: index,
                        bgOpacity: 0.7,
                        showHideOpacity: true
                    };

                    if (disableAnimation) {
                        options.showAnimationDuration = 0;
                    }

                    gallery = new PhotoSwipe(pswpElement, PhotoSwipeUI_Default, items, options);
                    gallery.init();
                };

                var galleryElements = document.querySelectorAll(gallerySelector);
                for (var i = 0, l = galleryElements.length; i < l; i++) {
                    galleryElements[i].setAttribute('data-pswp-uid', i + 1);
                    galleryElements[i].onclick = onThumbnailsClick;
                }
            };

            initPhotoSwipeFromDOM('.my-gallery');
        });
    </script>
</body>
</html>
